import React from 'react';
import { useParams } from 'react-router-dom';

const productsData = [
  // Consolidate all products into a single array for simplicity
  ...Array.from({ length: 5 }, (_, i) => ({
    id: i + 1,
    name: `Shirt ${i + 1}`,
    price: 20,
    imageUrl: `/images/shirt${i + 1}.jpg`,
    description: `This is Shirt ${i + 1}. A high-quality product!`,
  })),
  ...Array.from({ length: 5 }, (_, i) => ({
    id: i + 11,
    name: `T-shirt ${i + 1}`,
    price: 15,
    imageUrl: `./images/tshirt${i + 1}.jpg`,
    description: `This is T-shirt ${i + 1}. Perfect for casual wear.`,
  })),
];

function ProductDetails() {
  const { id } = useParams();
  const product = productsData.find((item) => item.id === parseInt(id));

  if (!product) {
    return <h2>Product not found!</h2>;
  }

  return (
    <div style={{ textAlign: 'center', padding: '2rem' }}>
      <img
        src={product.imageUrl}
        alt={product.name}
        style={{ width: '300px', height: '300px', objectFit: 'cover' }}
      />
      <h2>{product.name}</h2>
      <p>{product.description}</p>
      <p>Price: ${product.price}</p>
      <button>Add to Cart</button>
    </div>
  );
}

export default ProductDetails;
