import React from 'react';
import './About.css';

function About() {
  return (
    <div className="about-container">
      <h2>About Us</h2>
      <p>
        Welcome to <span>Merchandise Mart</span>, where shopping meets simplicity and style. We are passionate about
        bringing you a curated selection of high-quality products designed to fit seamlessly into your lifestyle.
      </p>
      <p>
        Our mission is to create an effortless shopping experience, combining innovation and convenience with exceptional
        customer service. Whether you're searching for the latest trends, timeless classics, or everyday essentials, we've
        got you covered.
      </p>
      <p>
        At <span>Merchandise Mart</span>, we believe in more than just selling products—we aim to build a community. With a
        focus on sustainability, ethical sourcing, and customer satisfaction, we’re here to inspire and empower you to
        shop confidently.
      </p>
      <p>Thank you for choosing us. Together, we make shopping smarter, easier, and more enjoyable.</p>
    </div>
  );
}

export default About;
